def.body:

{% for name in names %}
      def {{ name.id }}
        config.{{ name.id }}
      end

      def {{ name.id }}=(value)
        config.{{ name.id }} = (value)
      end
    {% end %}


{% for dir in dirs %}
          {{ run("./yaml_embed", dir) }}
        {% end %}

if DEFAULT_PLURAL_RULES.has_key?(locale)
  DEFAULT_PLURAL_RULES[locale][:rule]
else
  if @plural_rules.has_key?(locale)
    @plural_rules[locale]
  else
    COMMON_PLURAL_RULES[:default][:rule]
  end
end
